select cast(L_COMMITDate as date), cast(L_SHIPDate as date) from lineitem where L_SHIPMODE = 'TRUCK' and ((cast(L_SHIPDate as date) between date '1996-06-10' and date '1996-06-15') or (cast(L_SHIPDate as date) between date '1996-05-10' and date '1996-05-15') or (cast(L_SHIPDate as date) between date '1996-04-10' and date '1996-04-15') or (cast(L_SHIPDate as date) between date '1996-03-10' and date '1996-03-15') or (cast(L_SHIPDate as date) between date '1996-02-10' and date '1996-02-15') or (cast(L_SHIPDate as date) between date '1996-01-10' and date '1996-01-15')) and (L_DISCOUNT > 0.01 and L_DISCOUNT < 0.05) and (L_TAX >0.01 and L_TAX <0.05)  ORDER BY 1, 2 LIMIT 100;
